
public class ObjectTypeCast 
{

	public static void main(String[] args) 
	{
		// TODO Auto-generated method stub
		System.out.println("---------------------");
		System.out.println("Fundmental variable Type Casting");
		System.out.println("---------------------");
		int a=10;
		float b=a;//implicit type casting.
		System.out.println("value of a(int)="+a);
		System.out.println("value of b(float)="+b);
		float x1=10.75f;
		int x2=(int)x1;//explicit type casting.
		System.out.println("---------------------");
        System.out.println("value of x1(float)="+x1);
		System.out.println("---------------------");

		System.out.println("value of x2(int)="+x2);
		
		
		
	}

}
